-- Make sure ID sequences are valid:

CREATE OR REPLACE FUNCTION sequence_tables()
  RETURNS BOOL AS $$
  DECLARE setSequences BOOL;
  DECLARE access_count INT;
  DECLARE role_permissions_count INT;
  DECLARE user_profile_template_count INT;
BEGIN

  SELECT COUNT(*) INTO access_count FROM rp_master.access;
  -- raise notice 'access count: %', access_count;

	IF access_count = 2 THEN
	  ALTER SEQUENCE rp_master.access_id_seq RESTART WITH 3;
	END IF;

  SELECT COUNT(*) INTO role_permissions_count FROM rp_master.role_permissions;
  -- raise notice 'role_permissions count: %', role_permissions_count;

	IF role_permissions_count = 5 THEN
	  ALTER SEQUENCE rp_master.role_permissions_id_seq RESTART WITH 6;
	END IF;

  SELECT COUNT(*) INTO user_profile_template_count FROM rp_master.user_profile_template;
  -- raise notice 'user_profile_template count: %', user_profile_template_count;

	IF user_profile_template_count = 5 THEN
	  ALTER SEQUENCE rp_master.user_profile_template_id_seq RESTART WITH 6;
	END IF;

	IF (access_count = 2 AND role_permissions_count = 5 AND user_profile_template_count = 5) THEN
	  setSequences = true;
	 ELSE
	 	setSequences = false;
	END IF;
  RETURN setSequences;
END
$$ LANGUAGE plpgsql;

SELECT sequence_tables();

DROP FUNCTION IF EXISTS sequence_tables();
